using System; using System.IO; using Trabalho_parte_I.iOS; using Xamarin.Forms;  [assembly: Dependency(typeof(SQLite_iOS))] namespace Trabalho_parte_I.iOS { 	public class SQLite_iOS : ISQLite 	{ 		public SQLite_iOS() { } 		public SQLite.SQLiteConnection GetConnection() 		{ 			var sqliteFilename = "Trabalho_parte_I.db3"; 			string documentsPath = Environment.GetFolderPath(Environment.SpecialFolder.Personal); // Documents folder 			string libraryPath = Path.Combine(documentsPath, "..", "Library"); // Library folder 			var path = Path.Combine(libraryPath, sqliteFilename); 			var conn = new SQLite.SQLiteConnection(path); 			return conn; 		} 	} }